import React from 'react'
import { useState } from 'react'

export default function Form() {
    const [name,setName]=useState("")
    const [des,setDes]=useState("")
    const [date,setDate]=useState("")
    const [error,setError]=useState(null)

    const sumbitFunction=async(e)=>{
        e.preventDefault()
        const notice={name,des,date}
        const response=await fetch("/api/notices",{
            method:'POST',
            body:JSON.stringify(notice),
            headers:{
                "Content-Type":"json"
            }
        })

        const json=await response.json()

        if(!response.ok){
            setError(json.error)
        }
        else{
            setName("")
            setDes("")
            setDate("")
            setError(null)
            console.log(json)
        }
    }

  return (
    <div className="form">
        <div className="formHeading">
            Create new event
        </div>
        <div className="eventNameF">
            <label className='inputHeading'>Event Name:</label>
            <input type="text" placeholder="Enter event name" onChange={(e)=>setName(e.target.value)} value={name} />
        </div>
        <div className="eventDesF">
            <label className='inputHeading'>Event Description:</label>
            <input type="text" placeholder="Enter description of event" onChange={(e)=>setDes(e.target.value)} value={des}/>
        </div>
        <div className="dateF">
            <label className='inputHeading'>Event Date:</label>
            <input type="text" placeholder='Event date' onChange={(e)=>setDate(e.target.value)} value={date} />
        </div>
        <button onClick={sumbitFunction}>Create Notice</button>
    </div>
  )
}
